/**
 * üîß ExamFlow Service Worker
 * –ö—ç—à–∏—Ä–æ–≤–∞–Ω–∏–µ —Ä–µ—Å—É—Ä—Å–æ–≤ –¥–ª—è –±—ã—Å—Ç—Ä–æ–π –∑–∞–≥—Ä—É–∑–∫–∏ –∏ –æ—Ñ–ª–∞–π–Ω —Ä–∞–±–æ—Ç—ã
 */

const CACHE_NAME = 'examflow-v3.0';
const STATIC_CACHE = 'examflow-static-v3.0';

// –†–µ—Å—É—Ä—Å—ã –¥–ª—è –∫—ç—à–∏—Ä–æ–≤–∞–Ω–∏—è (—Ç–æ–ª—å–∫–æ –ª–æ–∫–∞–ª—å–Ω—ã–µ)
const CACHE_RESOURCES = [
  '/',
  '/static/css/examflow-unified.css',
  '/static/js/examflow-main.js'
];

// –í–Ω–µ—à–Ω–∏–µ —Ä–µ—Å—É—Ä—Å—ã –∫—ç—à–∏—Ä—É—é—Ç—Å—è –æ—Ç–¥–µ–ª—å–Ω–æ
const EXTERNAL_RESOURCES = [
  'https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap',
  'https://fonts.gstatic.com'
];

// –£—Å—Ç–∞–Ω–æ–≤–∫–∞ Service Worker
self.addEventListener('install', (event) => {
  console.log('üîß Service Worker: —É—Å—Ç–∞–Ω–æ–≤–∫–∞');
  
  event.waitUntil(
    Promise.all([
      // –ö—ç—à–∏—Ä—É–µ–º –ª–æ–∫–∞–ª—å–Ω—ã–µ —Ä–µ—Å—É—Ä—Å—ã
      caches.open(STATIC_CACHE).then((cache) => {
        console.log('üì¶ –ö—ç—à–∏—Ä–æ–≤–∞–Ω–∏–µ –ª–æ–∫–∞–ª—å–Ω—ã—Ö —Ä–µ—Å—É—Ä—Å–æ–≤');
        return cache.addAll(CACHE_RESOURCES);
      }),
      // –ö—ç—à–∏—Ä—É–µ–º –≤–Ω–µ—à–Ω–∏–µ —Ä–µ—Å—É—Ä—Å—ã –æ—Ç–¥–µ–ª—å–Ω–æ (–µ—Å–ª–∏ –¥–æ—Å—Ç—É–ø–Ω—ã)
      caches.open('external-cache').then((cache) => {
        console.log('üåê –ö—ç—à–∏—Ä–æ–≤–∞–Ω–∏–µ –≤–Ω–µ—à–Ω–∏—Ö —Ä–µ—Å—É—Ä—Å–æ–≤');
        return Promise.allSettled(
          EXTERNAL_RESOURCES.map(url => 
            fetch(url, { mode: 'cors' })
              .then(response => response.ok ? cache.put(url, response) : null)
              .catch(() => console.log('‚ö†Ô∏è –í–Ω–µ—à–Ω–∏–π —Ä–µ—Å—É—Ä—Å –Ω–µ–¥–æ—Å—Ç—É–ø–µ–Ω:', url))
          )
        );
      })
    ]).catch((error) => {
      console.error('‚ùå –û—à–∏–±–∫–∞ –∫—ç—à–∏—Ä–æ–≤–∞–Ω–∏—è:', error);
    })
  );
  
  // –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–∞—è –∞–∫—Ç–∏–≤–∞—Ü–∏—è –Ω–æ–≤–æ–≥–æ SW
  self.skipWaiting();
});

// –ê–∫—Ç–∏–≤–∞—Ü–∏—è Service Worker
self.addEventListener('activate', (event) => {
  console.log('üöÄ Service Worker: –∞–∫—Ç–∏–≤–∞—Ü–∏—è');
  
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          // –£–¥–∞–ª—è–µ–º —Å—Ç–∞—Ä—ã–µ –∫—ç—à–∏
          if (cacheName !== CACHE_NAME && cacheName !== STATIC_CACHE) {
            console.log('üßπ –£–¥–∞–ª–µ–Ω–∏–µ —Å—Ç–∞—Ä–æ–≥–æ –∫—ç—à–∞:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
  
  // –ó–∞—Ö–≤–∞—Ç—ã–≤–∞–µ–º –∫–æ–Ω—Ç—Ä–æ–ª—å –Ω–∞–¥ –≤—Å–µ–º–∏ –∫–ª–∏–µ–Ω—Ç–∞–º–∏
  self.clients.claim();
});

// –û–±—Ä–∞–±–æ—Ç–∫–∞ –∑–∞–ø—Ä–æ—Å–æ–≤
self.addEventListener('fetch', (event) => {
  const { request } = event;
  const url = new URL(request.url);
  
  // –ò–≥–Ω–æ—Ä–∏—Ä—É–µ–º –∑–∞–ø—Ä–æ—Å—ã –∫ API –∏ –∞–¥–º–∏–Ω–∫–µ
  if (url.pathname.startsWith('/api/') || 
      url.pathname.startsWith('/admin/') || 
      url.pathname.startsWith('/bot/')) {
    return;
  }
  
  event.respondWith(
    caches.match(request)
      .then((response) => {
        // –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∏–∑ –∫—ç—à–∞ –µ—Å–ª–∏ –µ—Å—Ç—å
        if (response) {
          return response;
        }
        
        // –ò–Ω–∞—á–µ –∑–∞–≥—Ä—É–∂–∞–µ–º –∏–∑ —Å–µ—Ç–∏
        return fetch(request)
          .then((response) => {
            // –ü—Ä–æ–≤–µ—Ä—è–µ–º –≤–∞–ª–∏–¥–Ω–æ—Å—Ç—å –æ—Ç–≤–µ—Ç–∞
            if (!response || response.status !== 200 || response.type !== 'basic') {
              return response;
            }
            
            // –ö–ª–æ–Ω–∏—Ä—É–µ–º –æ—Ç–≤–µ—Ç –¥–ª—è –∫—ç—à–∏—Ä–æ–≤–∞–Ω–∏—è
            const responseToCache = response.clone();
            
            // –ö—ç—à–∏—Ä—É–µ–º —Ç–æ–ª—å–∫–æ GET –∑–∞–ø—Ä–æ—Å—ã
            if (request.method === 'GET') {
              caches.open(CACHE_NAME)
                .then((cache) => {
                  cache.put(request, responseToCache);
                });
            }
            
            return response;
          })
          .catch(() => {
            // –û—Ñ–ª–∞–π–Ω fallback
            if (request.destination === 'document') {
              return caches.match('/');
            }
          });
      })
  );
});

// –û–±—Ä–∞–±–æ—Ç–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏–π –æ—Ç –≥–ª–∞–≤–Ω–æ–≥–æ –ø–æ—Ç–æ–∫–∞
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'SKIP_WAITING') {
    self.skipWaiting();
  }
});

// –£–≤–µ–¥–æ–º–ª–µ–Ω–∏—è –æ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è—Ö
self.addEventListener('message', (event) => {
  if (event.data && event.data.type === 'GET_VERSION') {
    event.ports[0].postMessage({ version: CACHE_NAME });
  }
});
