/**
 * ðŸš€ ExamFlow - ÐžÑÐ½Ð¾Ð²Ð½Ð¾Ð¹ JavaScript Ð¼Ð¾Ð´ÑƒÐ»ÑŒ
 * Ð¡Ð¾Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ð°Ñ Ð¸Ð½Ñ‚ÐµÑ€Ð°ÐºÑ‚Ð¸Ð²Ð½Ð¾ÑÑ‚ÑŒ Ð´Ð»Ñ Ð¾Ð±Ñ€Ð°Ð·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒÐ½Ð¾Ð¹ Ð¿Ð»Ð°Ñ‚Ñ„Ð¾Ñ€Ð¼Ñ‹
 * ES2025+ ÑÑ‚Ð°Ð½Ð´Ð°Ñ€Ñ‚Ñ‹ Ñ ÑÐ¾Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ñ‹Ð¼Ð¸ API
 */

class ExamFlowApp {
  #isLoading = false;  // ÐŸÑ€Ð¸Ð²Ð°Ñ‚Ð½Ð¾Ðµ Ð¿Ð¾Ð»Ðµ
  #aiChat = null;
  #intersectionObserver = null;
  #abortController = new AbortController();

  constructor() {
    this.init();
  }

  init() {
    this.setupEventListeners();
    this.setupIntersectionObserver();
    this.setupKeyboardNavigation();
    this.initializeComponents();
  }

  // ===== ÐÐÐ¡Ð¢Ð ÐžÐ™ÐšÐ Ð¡ÐžÐ‘Ð«Ð¢Ð˜Ð™ =====
  setupEventListeners() {
    // AI Ð¸Ð½Ñ‚ÐµÑ€Ñ„ÐµÐ¹Ñ
    const aiInput = document.getElementById('aiInput');
    const aiSendBtn = document.querySelector('.ai-send-btn');
    
    if (aiInput) {
      aiInput.addEventListener('keypress', (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
          e.preventDefault();
          this.sendAIQuery();
        }
      });
      
      // ÐÐ²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¾Ðµ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ðµ Ñ€Ð°Ð·Ð¼ÐµÑ€Ð°
      aiInput.addEventListener('input', this.autoResizeInput.bind(this));
    }
    
    if (aiSendBtn) {
      aiSendBtn.addEventListener('click', () => this.sendAIQuery());
    }

    // Ð‘Ñ‹ÑÑ‚Ñ€Ñ‹Ðµ Ð²Ð¾Ð¿Ñ€Ð¾ÑÑ‹
    document.querySelectorAll('.ai-suggestion').forEach(btn => {
      btn.addEventListener('click', () => {
        const question = btn.textContent.trim();
        this.askQuestion(question);
      });
    });

    // ÐŸÐ»Ð°Ð²Ð½Ð°Ñ Ð¿Ñ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÐ° Ð´Ð»Ñ ÑÐºÐ¾Ñ€Ð½Ñ‹Ñ… ÑÑÑ‹Ð»Ð¾Ðº
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
      anchor.addEventListener('click', (e) => {
        e.preventDefault();
        const targetId = anchor.getAttribute('href').substring(1);
        const target = document.getElementById(targetId);
        
        if (target) {
          target.scrollIntoView({
            behavior: 'smooth',
            block: 'start'
          });
        }
      });
    });

    // ÐœÐ¾Ð±Ð¸Ð»ÑŒÐ½Ð¾Ðµ Ð¼ÐµÐ½ÑŽ
    const mobileToggle = document.querySelector('.mobile-menu-toggle');
    if (mobileToggle) {
      mobileToggle.addEventListener('click', this.toggleMobileMenu.bind(this));
    }

    // Ð—Ð°ÐºÑ€Ñ‹Ñ‚Ð¸Ðµ Ð¼Ð¾Ð±Ð¸Ð»ÑŒÐ½Ð¾Ð³Ð¾ Ð¼ÐµÐ½ÑŽ Ð¿Ñ€Ð¸ ÐºÐ»Ð¸ÐºÐµ Ð²Ð½Ðµ ÐµÐ³Ð¾
    document.addEventListener('click', (e) => {
      const mobileMenu = document.getElementById('mobileMenuOverlay');
      const toggle = document.querySelector('.mobile-menu-toggle');
      
      if (mobileMenu && mobileMenu.classList.contains('active') && 
          !mobileMenu.contains(e.target) && !toggle.contains(e.target)) {
        this.toggleMobileMenu();
      }
    });
  }

  // ===== AI Ð¤Ð£ÐÐšÐ¦Ð˜ÐžÐÐÐ›Ð¬ÐÐžÐ¡Ð¢Ð¬ =====
  async sendAIQuery() {
    const input = document.getElementById('aiInput');
    const chat = document.getElementById('aiChat');
    
    if (!input || !input.value.trim() || this.#isLoading) return;
    
    const question = input.value.trim();
    input.value = '';
    this.#isLoading = true;
    
    // ÐŸÐ¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ñ‡Ð°Ñ‚ ÐµÑÐ»Ð¸ ÑÐºÑ€Ñ‹Ñ‚
    if (chat) {
      chat.style.display = 'block';
    }
    
    // Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Ð²Ð¾Ð¿Ñ€Ð¾Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
    this.addMessage('user', question);
    
    // ÐŸÐ¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ð¸Ð½Ð´Ð¸ÐºÐ°Ñ‚Ð¾Ñ€ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸
    const loadingId = this.addMessage('assistant', 'ÐÐ½Ð°Ð»Ð¸Ð·Ð¸Ñ€ÑƒÑŽ Ð²Ð°Ñˆ Ð²Ð¾Ð¿Ñ€Ð¾Ñ...', true);
    
    try {
      const response = await fetch('/ai/api/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': this.getCSRFToken()
        },
        body: JSON.stringify({ prompt: question })
      });
      
      if (!response.ok) {
        throw new Error(`HTTP ${response.status}`);
      }
      
      const data = await response.json();
      
      // Ð£Ð´Ð°Ð»ÑÐµÐ¼ Ð¸Ð½Ð´Ð¸ÐºÐ°Ñ‚Ð¾Ñ€ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸
      this.removeMessage(loadingId);
      
      if (data.answer) {
        this.addMessage('assistant', data.answer);
        
        // Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Ð¸ÑÑ‚Ð¾Ñ‡Ð½Ð¸ÐºÐ¸ ÐµÑÐ»Ð¸ ÐµÑÑ‚ÑŒ
        if (data.sources && data.sources.length > 0) {
          const sourcesText = 'ðŸ“š Ð˜ÑÑ‚Ð¾Ñ‡Ð½Ð¸ÐºÐ¸:\n' + data.sources.slice(0, 3).join('\n');
          this.addMessage('assistant', sourcesText, false, 'sources');
        }
      } else if (data.error) {
        this.addMessage('assistant', `âŒ ÐžÑˆÐ¸Ð±ÐºÐ°: ${data.error}`);
      } else {
        this.addMessage('assistant', 'Ð˜Ð·Ð²Ð¸Ð½Ð¸Ñ‚Ðµ, Ð½Ðµ ÑƒÐ´Ð°Ð»Ð¾ÑÑŒ Ð¿Ð¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ð¾Ñ‚Ð²ÐµÑ‚. ÐŸÐ¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ Ð¿ÐµÑ€ÐµÑ„Ð¾Ñ€Ð¼ÑƒÐ»Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ Ð²Ð¾Ð¿Ñ€Ð¾Ñ.');
      }
      
    } catch (error) {
      console.error('AI API Error:', error);
      this.removeMessage(loadingId);
      
      // ÐŸÑ€Ð¾Ð±ÑƒÐµÐ¼ ÑÐºÑÑ‚Ñ€ÐµÐ½Ð½Ñ‹Ð¹ API
      try {
        console.log('ðŸš¨ ÐŸÑ€Ð¾Ð±ÑƒÐµÐ¼ ÑÐºÑÑ‚Ñ€ÐµÐ½Ð½Ñ‹Ð¹ AI API...');
        const emergencyResponse = await fetch('/ai/emergency/', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': this.getCSRFToken()
          },
          body: JSON.stringify({ prompt: question })
        });
        
        if (emergencyResponse.ok) {
          const emergencyData = await emergencyResponse.json();
          if (emergencyData.answer) {
            this.addMessage('assistant', `ðŸš¨ ${emergencyData.answer}`);
            
            if (emergencyData.sources && emergencyData.sources.length > 0) {
              const sourcesText = 'ðŸ“š Ð˜ÑÑ‚Ð¾Ñ‡Ð½Ð¸ÐºÐ¸:\n' + emergencyData.sources.slice(0, 3).map(s => s.title).join('\n');
              this.addMessage('assistant', sourcesText, false, 'sources');
            }
          } else {
            this.addMessage('assistant', 'âŒ Ð­ÐºÑÑ‚Ñ€ÐµÐ½Ð½Ñ‹Ð¹ ÑÐµÑ€Ð²Ð¸Ñ Ñ‚Ð°ÐºÐ¶Ðµ Ð½ÐµÐ´Ð¾ÑÑ‚ÑƒÐ¿ÐµÐ½. ÐŸÐ¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ Ð¿Ð¾Ð·Ð¶Ðµ.');
          }
        } else {
          this.addMessage('assistant', 'âŒ ÐŸÑ€Ð¾Ð¸Ð·Ð¾ÑˆÐ»Ð° Ð¾ÑˆÐ¸Ð±ÐºÐ° ÑÐ¾ÐµÐ´Ð¸Ð½ÐµÐ½Ð¸Ñ. ÐŸÐ¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ Ð¿Ð¾Ð·Ð¶Ðµ.');
        }
      } catch (emergencyError) {
        console.error('Emergency AI API Error:', emergencyError);
        this.addMessage('assistant', 'âŒ Ð’ÑÐµ ÑÐµÑ€Ð²Ð¸ÑÑ‹ Ð˜Ð˜ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ð¾ Ð½ÐµÐ´Ð¾ÑÑ‚ÑƒÐ¿Ð½Ñ‹. ÐŸÐ¾Ð¿Ñ€Ð¾Ð±ÑƒÐ¹Ñ‚Ðµ Ð¿Ð¾Ð·Ð¶Ðµ Ð¸Ð»Ð¸ Ð²Ð¾ÑÐ¿Ð¾Ð»ÑŒÐ·ÑƒÐ¹Ñ‚ÐµÑÑŒ Ð¼Ð°Ñ‚ÐµÑ€Ð¸Ð°Ð»Ð°Ð¼Ð¸ Ð¤Ð˜ÐŸÐ˜.');
      }
    } finally {
      this.#isLoading = false;
    }
  }

  askQuestion(question) {
    const input = document.getElementById('aiInput');
    if (input) {
      input.value = question;
      input.focus();
      
      // ÐÐµÐ±Ð¾Ð»ÑŒÑˆÐ°Ñ Ð·Ð°Ð´ÐµÑ€Ð¶ÐºÐ° Ð´Ð»Ñ UX
      setTimeout(() => {
        this.sendAIQuery();
      }, 200);
    }
  }

  addMessage(type, text, isLoading = false, messageType = 'default') {
    const chat = document.getElementById('aiChat');
    if (!chat) return null;
    
    const messageId = 'msg-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9);
    
    const messageDiv = document.createElement('div');
    messageDiv.id = messageId;
    messageDiv.className = `ai-message ${type} ${messageType}`;
    
    if (isLoading) {
      messageDiv.innerHTML = `
        <div class="flex items-center gap-3">
          <div class="animate-pulse">ðŸ¤–</div>
          <span class="loading-text">${text}</span>
          <div class="loading-dots">
            <span>.</span><span>.</span><span>.</span>
          </div>
        </div>
      `;
    } else {
      const icon = type === 'user' ? 'ðŸ‘¤' : 'ðŸ¤–';
      const formattedText = this.formatMessage(text);
      
      messageDiv.innerHTML = `
        <div class="message-header">
          <span class="message-icon">${icon}</span>
          <span class="message-type">${type === 'user' ? 'Ð’Ñ‹' : 'Ð˜Ð˜-Ð°ÑÑÐ¸ÑÑ‚ÐµÐ½Ñ‚'}</span>
        </div>
        <div class="message-content">${formattedText}</div>
      `;
    }
    
    chat.appendChild(messageDiv);
    
    // ÐŸÐ»Ð°Ð²Ð½Ð°Ñ Ð°Ð½Ð¸Ð¼Ð°Ñ†Ð¸Ñ Ð¿Ð¾ÑÐ²Ð»ÐµÐ½Ð¸Ñ
    messageDiv.style.opacity = '0';
    messageDiv.style.transform = 'translateY(20px)';
    
    requestAnimationFrame(() => {
      messageDiv.style.transition = 'all 0.3s ease-out';
      messageDiv.style.opacity = '1';
      messageDiv.style.transform = 'translateY(0)';
    });
    
    // ÐŸÑ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÐ° Ðº Ð½Ð¾Ð²Ð¾Ð¼Ñƒ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸ÑŽ
    setTimeout(() => {
      messageDiv.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
    }, 100);
    
    return messageId;
  }

  removeMessage(messageId) {
    const message = document.getElementById(messageId);
    if (message) {
      message.style.transition = 'all 0.2s ease-out';
      message.style.opacity = '0';
      message.style.transform = 'translateY(-10px)';
      
      setTimeout(() => {
        message.remove();
      }, 200);
    }
  }

  formatMessage(text) {
    // ÐŸÑ€Ð¾ÑÑ‚Ð¾Ðµ Ñ„Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ Ñ‚ÐµÐºÑÑ‚Ð°
    return text
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
      .replace(/\*(.*?)\*/g, '<em>$1</em>')
      .replace(/`(.*?)`/g, '<code>$1</code>')
      .replace(/\n/g, '<br>');
  }

  // ===== Ð£Ð¢Ð˜Ð›Ð˜Ð¢Ð« =====
  autoResizeInput(e) {
    const input = e.target;
    input.style.height = 'auto';
    input.style.height = Math.min(input.scrollHeight, 120) + 'px';
  }

  getCSRFToken() {
    const cookies = document.cookie.split(';');
    for (let cookie of cookies) {
      const [name, value] = cookie.trim().split('=');
      if (name === 'csrftoken') {
        return decodeURIComponent(value);
      }
    }
    
    // Fallback: Ð¿Ð¾Ð¸ÑÐº Ð² Ð¼ÐµÑ‚Ð°-Ñ‚ÐµÐ³Ð°Ñ…
    const csrfMeta = document.querySelector('meta[name="csrf-token"]');
    return csrfMeta ? csrfMeta.getAttribute('content') : '';
  }

  // ===== ÐœÐžÐ‘Ð˜Ð›Ð¬ÐÐžÐ• ÐœÐ•ÐÐ® =====
  toggleMobileMenu() {
    const overlay = document.getElementById('mobileMenuOverlay');
    if (overlay) {
      overlay.classList.toggle('active');
      
      // Ð‘Ð»Ð¾ÐºÐ¸Ñ€ÑƒÐµÐ¼ Ð¿Ñ€Ð¾ÐºÑ€ÑƒÑ‚ÐºÑƒ body Ð¿Ñ€Ð¸ Ð¾Ñ‚ÐºÑ€Ñ‹Ñ‚Ð¾Ð¼ Ð¼ÐµÐ½ÑŽ
      if (overlay.classList.contains('active')) {
        document.body.style.overflow = 'hidden';
      } else {
        document.body.style.overflow = '';
      }
    }
  }

  // ===== ÐÐÐ˜ÐœÐÐ¦Ð˜Ð˜ ÐŸÐ Ð˜ Ð¡ÐšÐ ÐžÐ›Ð›Ð• =====
  setupIntersectionObserver() {
    const observerOptions = {
      threshold: 0.1,
      rootMargin: '0px 0px -50px 0px'
    };
    
    this.#intersectionObserver = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('animate-fade-in');
          
          // Ð”Ð¾Ð±Ð°Ð²Ð»ÑÐµÐ¼ Ð·Ð°Ð´ÐµÑ€Ð¶ÐºÑƒ Ð´Ð»Ñ ÐºÐ°ÑÐºÐ°Ð´Ð½Ð¾Ð¹ Ð°Ð½Ð¸Ð¼Ð°Ñ†Ð¸Ð¸
          const delay = Array.from(entry.target.parentNode.children).indexOf(entry.target) * 100;
          entry.target.style.animationDelay = `${delay}ms`;
        }
      });
    }, observerOptions);
    
    // ÐÐ°Ð±Ð»ÑŽÐ´Ð°ÐµÐ¼ Ð·Ð° ÐºÐ°Ñ€Ñ‚Ð¾Ñ‡ÐºÐ°Ð¼Ð¸ Ð¸ ÑÐµÐºÑ†Ð¸ÑÐ¼Ð¸
    document.querySelectorAll('.card, .feature-card, .stat-card').forEach(el => {
      this.#intersectionObserver.observe(el);
    });
  }

  // ÐžÑ‡Ð¸ÑÑ‚ÐºÐ° Ñ€ÐµÑÑƒÑ€ÑÐ¾Ð²
  destroy() {
    this.#abortController.abort();
    if (this.#intersectionObserver) {
      this.#intersectionObserver.disconnect();
    }
  }

  // ===== Ð”ÐžÐ¡Ð¢Ð£ÐŸÐÐžÐ¡Ð¢Ð¬ =====
  setupKeyboardNavigation() {
    // ÐžÑ‚ÑÐ»ÐµÐ¶Ð¸Ð²Ð°Ð½Ð¸Ðµ Ð½Ð°Ð²Ð¸Ð³Ð°Ñ†Ð¸Ð¸ Ñ ÐºÐ»Ð°Ð²Ð¸Ð°Ñ‚ÑƒÑ€Ñ‹ Ð´Ð»Ñ ÑƒÐ»ÑƒÑ‡ÑˆÐµÐ½Ð½Ð¾Ð³Ð¾ Ñ„Ð¾ÐºÑƒÑÐ°
    let isUsingKeyboard = false;
    
    document.addEventListener('keydown', (e) => {
      if (e.key === 'Tab') {
        isUsingKeyboard = true;
        document.body.classList.add('using-keyboard');
      }
    });
    
    document.addEventListener('mousedown', () => {
      isUsingKeyboard = false;
      document.body.classList.remove('using-keyboard');
    });

    // Escape Ð´Ð»Ñ Ð·Ð°ÐºÑ€Ñ‹Ñ‚Ð¸Ñ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ñ‹Ñ… Ð¾ÐºÐ¾Ð½
    document.addEventListener('keydown', (e) => {
      if (e.key === 'Escape') {
        const mobileMenu = document.getElementById('mobileMenuOverlay');
        if (mobileMenu && mobileMenu.classList.contains('active')) {
          this.toggleMobileMenu();
        }
      }
    });
  }

  // ===== Ð˜ÐÐ˜Ð¦Ð˜ÐÐ›Ð˜Ð—ÐÐ¦Ð˜Ð¯ ÐšÐžÐœÐŸÐžÐÐ•ÐÐ¢ÐžÐ’ =====
  initializeComponents() {
    // Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð°Ñ†Ð¸Ñ AI Ñ‡Ð°Ñ‚Ð°
    this.#aiChat = document.getElementById('aiChat');
    
    // Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð°Ñ†Ð¸Ñ Ð¿Ñ€Ð¾Ð³Ñ€ÐµÑÑ-Ð±Ð°Ñ€Ð¾Ð² Ñ Ð°Ð½Ð¸Ð¼Ð°Ñ†Ð¸ÐµÐ¹
    this.animateProgressBars();
    
    // Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð°Ñ†Ð¸Ñ ÑÑ‡ÐµÑ‚Ñ‡Ð¸ÐºÐ¾Ð²
    this.animateCounters();
  }

  animateProgressBars() {
    const progressBars = document.querySelectorAll('.progress-bar');
    
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          const bar = entry.target;
          const width = bar.style.width || bar.getAttribute('data-width') || '0%';
          
          bar.style.width = '0%';
          setTimeout(() => {
            bar.style.width = width;
          }, 200);
        }
      });
    });
    
    progressBars.forEach(bar => observer.observe(bar));
  }

  animateCounters() {
    const counters = document.querySelectorAll('.stat-number');
    
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          this.animateCounter(entry.target);
        }
      });
    });
    
    counters.forEach(counter => observer.observe(counter));
  }

  animateCounter(element) {
    const text = element.textContent;
    const number = parseInt(text.replace(/[^\d]/g, ''));
    
    if (isNaN(number)) return;
    
    const duration = 2000;
    const steps = 60;
    const increment = number / steps;
    let current = 0;
    
    const timer = setInterval(() => {
      current += increment;
      
      if (current >= number) {
        current = number;
        clearInterval(timer);
      }
      
      const suffix = text.replace(/[\d,]/g, '');
      element.textContent = Math.floor(current).toLocaleString() + suffix;
    }, duration / steps);
  }

  // ===== Ð£Ð¢Ð˜Ð›Ð˜Ð¢Ð« =====
  showNotification(message, type = 'info') {
    const notification = document.createElement('div');
    notification.className = `notification notification-${type}`;
    notification.innerHTML = `
      <div class="notification-content">
        <span class="notification-icon">${this.getNotificationIcon(type)}</span>
        <span class="notification-message">${message}</span>
        <button class="notification-close" onclick="this.parentElement.parentElement.remove()">âœ•</button>
      </div>
    `;
    
    document.body.appendChild(notification);
    
    // ÐÐ²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡ÐµÑÐºÐ¾Ðµ ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ðµ Ñ‡ÐµÑ€ÐµÐ· 5 ÑÐµÐºÑƒÐ½Ð´
    setTimeout(() => {
      if (notification.parentNode) {
        notification.remove();
      }
    }, 5000);
  }

  getNotificationIcon(type) {
    const icons = {
      'info': 'â„¹ï¸',
      'success': 'âœ…',
      'warning': 'âš ï¸',
      'error': 'âŒ'
    };
    return icons[type] || icons.info;
  }

  // ===== Ð’ÐÐ•Ð¨ÐÐ˜Ð• ÐœÐ•Ð¢ÐžÐ”Ð« =====
  focusAI() {
    const aiInput = document.getElementById('aiInput');
    if (aiInput) {
      aiInput.focus();
      aiInput.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  }
}

// ===== Ð“Ð›ÐžÐ‘ÐÐ›Ð¬ÐÐ«Ð• Ð¤Ð£ÐÐšÐ¦Ð˜Ð˜ =====

// Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð°Ñ†Ð¸Ñ Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ñ
let examFlowApp;

document.addEventListener('DOMContentLoaded', () => {
  examFlowApp = new ExamFlowApp();
  console.log('ðŸš€ ExamFlow Ð¿Ñ€Ð¸Ð»Ð¾Ð¶ÐµÐ½Ð¸Ðµ Ð¸Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¾');
});

// Ð“Ð»Ð¾Ð±Ð°Ð»ÑŒÐ½Ñ‹Ðµ Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð´Ð»Ñ Ð¾Ð±Ñ€Ð°Ñ‚Ð½Ð¾Ð¹ ÑÐ¾Ð²Ð¼ÐµÑÑ‚Ð¸Ð¼Ð¾ÑÑ‚Ð¸
function focusAI() {
  if (examFlowApp) {
    examFlowApp.focusAI();
  }
}

function sendAIQuery() {
  if (examFlowApp) {
    examFlowApp.sendAIQuery();
  }
}

function askQuestion(question) {
  if (examFlowApp) {
    examFlowApp.askQuestion(question);
  }
}

function toggleMobileMenu() {
  if (examFlowApp) {
    examFlowApp.toggleMobileMenu();
  }
}

// ===== Ð”ÐžÐŸÐžÐ›ÐÐ˜Ð¢Ð•Ð›Ð¬ÐÐ«Ð• Ð£Ð¢Ð˜Ð›Ð˜Ð¢Ð« =====

// Throttle Ñ„ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¾Ð¿Ñ‚Ð¸Ð¼Ð¸Ð·Ð°Ñ†Ð¸Ð¸ Ð¿Ñ€Ð¾Ð¸Ð·Ð²Ð¾Ð´Ð¸Ñ‚ÐµÐ»ÑŒÐ½Ð¾ÑÑ‚Ð¸
function throttle(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

// Debounce Ñ„ÑƒÐ½ÐºÑ†Ð¸Ñ Ð´Ð»Ñ Ð¿Ð¾Ð¸ÑÐºÐ°
function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

// ÐŸÑ€Ð¾Ð²ÐµÑ€ÐºÐ° Ð¿Ð¾Ð´Ð´ÐµÑ€Ð¶ÐºÐ¸ ÑÐ¾Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ñ‹Ñ… Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¹
function checkBrowserSupport() {
  const features = {
    fetch: typeof fetch !== 'undefined',
    intersectionObserver: 'IntersectionObserver' in window,
    customProperties: CSS.supports('color', 'var(--test)'),
    grid: CSS.supports('display', 'grid'),
    flexbox: CSS.supports('display', 'flex')
  };
  
  console.log('ðŸ” ÐŸÐ¾Ð´Ð´ÐµÑ€Ð¶ÐºÐ° Ð±Ñ€Ð°ÑƒÐ·ÐµÑ€Ð°:', features);
  
  // ÐŸÐ¾ÐºÐ°Ð·Ñ‹Ð²Ð°ÐµÐ¼ Ð¿Ñ€ÐµÐ´ÑƒÐ¿Ñ€ÐµÐ¶Ð´ÐµÐ½Ð¸Ðµ Ð´Ð»Ñ ÑÑ‚Ð°Ñ€Ñ‹Ñ… Ð±Ñ€Ð°ÑƒÐ·ÐµÑ€Ð¾Ð²
  const unsupported = Object.entries(features).filter(([key, value]) => !value);
  if (unsupported.length > 0) {
    console.warn('âš ï¸ ÐÐµÐºÐ¾Ñ‚Ð¾Ñ€Ñ‹Ðµ Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð¼Ð¾Ð³ÑƒÑ‚ Ñ€Ð°Ð±Ð¾Ñ‚Ð°Ñ‚ÑŒ Ð½ÐµÐºÐ¾Ñ€Ñ€ÐµÐºÑ‚Ð½Ð¾:', unsupported);
  }
  
  return features;
}

// Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð°Ñ†Ð¸Ñ Ð¿Ñ€Ð¾Ð²ÐµÑ€ÐºÐ¸ Ð±Ñ€Ð°ÑƒÐ·ÐµÑ€Ð°
document.addEventListener('DOMContentLoaded', checkBrowserSupport);

// ===== Ð¡Ð•Ð Ð’Ð˜Ð¡ Ð’ÐžÐ ÐšÐ•Ð  (Ð´Ð»Ñ PWA Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¾Ð½Ð°Ð»ÑŒÐ½Ð¾ÑÑ‚Ð¸) =====
if ('serviceWorker' in navigator) {
  window.addEventListener('load', () => {
    navigator.serviceWorker.register('/static/js/sw.js')
      .then((registration) => {
        console.log('ðŸ”§ Service Worker Ð·Ð°Ñ€ÐµÐ³Ð¸ÑÑ‚Ñ€Ð¸Ñ€Ð¾Ð²Ð°Ð½:', registration.scope);
      })
      .catch((error) => {
        console.log('âŒ ÐžÑˆÐ¸Ð±ÐºÐ° Ñ€ÐµÐ³Ð¸ÑÑ‚Ñ€Ð°Ñ†Ð¸Ð¸ Service Worker:', error);
      });
  });
}

/**
 * ExamFlow Performance Optimizations
