/**
 * ðŸ”Œ ExamFlow API Client
 * Ð¡Ð¾Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ñ‹Ð¹ ÐºÐ»Ð¸ÐµÐ½Ñ‚ Ð´Ð»Ñ Ñ€Ð°Ð±Ð¾Ñ‚Ñ‹ Ñ API (ES2025+)
 */

class APIClient {
  #baseURL;
  #defaultHeaders;
  #abortController;

  constructor(baseURL = '') {
    this.#baseURL = baseURL;
    this.#defaultHeaders = {
      'Content-Type': 'application/json',
    };
    this.#abortController = new AbortController();
  }

  /**
   * ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÑ‚ CSRF Ñ‚Ð¾ÐºÐµÐ½ Ð¸Ð· cookies Ð¸Ð»Ð¸ meta-Ñ‚ÐµÐ³Ð¾Ð²
   * @returns {string} CSRF Ñ‚Ð¾ÐºÐµÐ½
   */
  #getCSRFToken() {
    // Ð¡Ð½Ð°Ñ‡Ð°Ð»Ð° Ð¸Ñ‰ÐµÐ¼ Ð² cookies
    const cookies = document.cookie.split(';');
    for (const cookie of cookies) {
      const [name, value] = cookie.trim().split('=');
      if (name === 'csrftoken') {
        return decodeURIComponent(value);
      }
    }
    
    // Fallback: Ð¿Ð¾Ð¸ÑÐº Ð² meta-Ñ‚ÐµÐ³Ð°Ñ…
    const csrfMeta = document.querySelector('meta[name="csrf-token"]');
    return csrfMeta?.getAttribute('content') ?? '';
  }

  /**
   * Ð’Ñ‹Ð¿Ð¾Ð»Ð½ÑÐµÑ‚ HTTP Ð·Ð°Ð¿Ñ€Ð¾Ñ Ñ ÑÐ¾Ð²Ñ€ÐµÐ¼ÐµÐ½Ð½Ñ‹Ð¼Ð¸ ÑÑ‚Ð°Ð½Ð´Ð°Ñ€Ñ‚Ð°Ð¼Ð¸
   * @param {string} endpoint - ÐšÐ¾Ð½ÐµÑ‡Ð½Ð°Ñ Ñ‚Ð¾Ñ‡ÐºÐ° API
   * @param {Object} options - ÐžÐ¿Ñ†Ð¸Ð¸ Ð·Ð°Ð¿Ñ€Ð¾ÑÐ°
   * @returns {Promise<Object>} ÐžÑ‚Ð²ÐµÑ‚ API
   */
  async #makeRequest(endpoint, options = {}) {
    const url = new URL(endpoint, this.#baseURL);
    
    const config = {
      headers: {
        ...this.#defaultHeaders,
        'X-CSRFToken': this.#getCSRFToken(),
        ...options.headers,
      },
      signal: this.#abortController.signal,
      ...options,
    };

    try {
      const response = await fetch(url, config);
      
      if (!response.ok) {
        throw new APIError(
          `HTTP ${response.status}: ${response.statusText}`,
          response.status,
          await response.text()
        );
      }
      
      const contentType = response.headers.get('content-type');
      if (contentType?.includes('application/json')) {
        return await response.json();
      }
      
      return await response.text();
      
    } catch (error) {
      if (error.name === 'AbortError') {
        throw new APIError('Ð—Ð°Ð¿Ñ€Ð¾Ñ Ð¾Ñ‚Ð¼ÐµÐ½ÐµÐ½', 0, 'Request aborted');
      }
      
      if (error instanceof APIError) {
        throw error;
      }
      
      throw new APIError('ÐžÑˆÐ¸Ð±ÐºÐ° ÑÐµÑ‚Ð¸', 0, error.message);
    }
  }

  /**
   * ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð»ÑÐµÑ‚ Ð²Ð¾Ð¿Ñ€Ð¾Ñ Ðº AI Ð°ÑÑÐ¸ÑÑ‚ÐµÐ½Ñ‚Ñƒ
   * @param {string} prompt - Ð’Ð¾Ð¿Ñ€Ð¾Ñ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
   * @returns {Promise<Object>} ÐžÑ‚Ð²ÐµÑ‚ AI
   */
  async askAI(prompt) {
    if (!prompt?.trim()) {
      throw new APIError('ÐŸÑƒÑÑ‚Ð¾Ð¹ Ð·Ð°Ð¿Ñ€Ð¾Ñ', 400, 'Prompt is required');
    }

    return await this.#makeRequest('/ai/api/', {
      method: 'POST',
      body: JSON.stringify({ prompt: prompt.trim() }),
    });
  }

  /**
   * ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÑ‚ ÑÐ¿Ð¸ÑÐ¾Ðº Ð¿Ñ€ÐµÐ´Ð¼ÐµÑ‚Ð¾Ð²
   * @returns {Promise<Array>} Ð¡Ð¿Ð¸ÑÐ¾Ðº Ð¿Ñ€ÐµÐ´Ð¼ÐµÑ‚Ð¾Ð²
   */
  async getSubjects() {
    return await this.#makeRequest('/api/subjects/');
  }

  /**
   * ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÑ‚ ÑÐ»ÑƒÑ‡Ð°Ð¹Ð½ÑƒÑŽ Ð·Ð°Ð´Ð°Ñ‡Ñƒ
   * @param {number} subjectId - ID Ð¿Ñ€ÐµÐ´Ð¼ÐµÑ‚Ð° (Ð¾Ð¿Ñ†Ð¸Ð¾Ð½Ð°Ð»ÑŒÐ½Ð¾)
   * @returns {Promise<Object>} Ð—Ð°Ð´Ð°Ñ‡Ð°
   */
  async getRandomTask(subjectId = null) {
    const endpoint = subjectId 
      ? `/api/tasks/random/?subject=${subjectId}`
      : '/api/tasks/random/';
    
    return await this.#makeRequest(endpoint);
  }

  /**
   * ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð»ÑÐµÑ‚ Ð¾Ñ‚Ð²ÐµÑ‚ Ð½Ð° Ð·Ð°Ð´Ð°Ñ‡Ñƒ
   * @param {number} taskId - ID Ð·Ð°Ð´Ð°Ñ‡Ð¸
   * @param {string} answer - ÐžÑ‚Ð²ÐµÑ‚ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
   * @returns {Promise<Object>} Ð ÐµÐ·ÑƒÐ»ÑŒÑ‚Ð°Ñ‚ Ð¿Ñ€Ð¾Ð²ÐµÑ€ÐºÐ¸
   */
  async submitAnswer(taskId, answer) {
    return await this.#makeRequest(`/api/tasks/${taskId}/submit/`, {
      method: 'POST',
      body: JSON.stringify({ answer }),
    });
  }

  /**
   * ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÑ‚ ÑÑ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÑƒ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
   * @returns {Promise<Object>} Ð¡Ñ‚Ð°Ñ‚Ð¸ÑÑ‚Ð¸ÐºÐ°
   */
  async getUserStats() {
    return await this.#makeRequest('/api/user/stats/');
  }

  /**
   * ÐžÑ‚Ð¼ÐµÐ½ÑÐµÑ‚ Ð²ÑÐµ Ð°ÐºÑ‚Ð¸Ð²Ð½Ñ‹Ðµ Ð·Ð°Ð¿Ñ€Ð¾ÑÑ‹
   */
  abort() {
    this.#abortController.abort();
    this.#abortController = new AbortController();
  }
}

/**
 * ÐšÐ°ÑÑ‚Ð¾Ð¼Ð½Ñ‹Ð¹ ÐºÐ»Ð°ÑÑ Ð¾ÑˆÐ¸Ð±Ð¾Ðº API
 */
class APIError extends Error {
  constructor(message, status = 0, details = '') {
    super(message);
    this.name = 'APIError';
    this.status = status;
    this.details = details;
  }

  toString() {
    return `${this.name}: ${this.message} (${this.status})`;
  }
}

