function showFeatureComingSoon(){alert('Функция решения задач будет добавлена позже');}function startTask(taskId){console.log('Запуск задачи:', taskId);window.location.href = `/tasks/${taskId}/`;}function reloadPage(){location.reload();}function continueTopic(topicId){console.log('Продолжение темы:', topicId);window.location.href = `/topics/${topicId}/continue/`;}function startTopic(topicId){console.log('Начало темы:', topicId);window.location.href = `/topics/${topicId}/start/`;}function startPractice(topicId){console.log('Начало практики по теме:', topicId);window.location.href = `/topics/${topicId}/practice/`;}function showTheory(topicId){console.log('Показать теорию по теме:', topicId);window.location.href = `/topics/${topicId}/theory/`;}function filterTasks(difficulty){console.log('Фильтрация задач по сложности:', difficulty);document.querySelectorAll('.btn-outline-secondary, .btn-outline-success, .btn-outline-warning, .btn-outline-danger').forEach(btn =>{btn.classList.remove('btn-outline-secondary', 'btn-outline-success', 'btn-outline-warning', 'btn-outline-danger');btn.classList.add('btn-outline-secondary');});const activeButton = event.target;activeButton.classList.remove('btn-outline-secondary');if (difficulty === 'easy'){activeButton.classList.add('btn-outline-success');}else if (difficulty === 'medium'){activeButton.classList.add('btn-outline-warning');}else if (difficulty === 'hard'){activeButton.classList.add('btn-outline-danger');}else{activeButton.classList.add('btn-outline-secondary');}const taskCards = document.querySelectorAll('.task-card');taskCards.forEach(card =>{if (difficulty === 'all' || card.dataset.difficulty === difficulty){card.style.display = 'block';}else{card.style.display = 'none';}});}document.addEventListener('DOMContentLoaded', function(){console.log('Core functions initialized');addEventListeners();});function addEventListeners(){document.querySelectorAll('[onclick*="alert"]').forEach(btn =>{btn.removeAttribute('onclick');btn.addEventListener('click', showFeatureComingSoon);});document.querySelectorAll('[onclick*="startTask"]').forEach(btn =>{const taskId = btn.getAttribute('data-task-id') || btn.closest('[data-task-id]')?.dataset.taskId;btn.removeAttribute('onclick');btn.addEventListener('click', () => startTask(taskId));});document.querySelectorAll('[onclick*="location.reload"]').forEach(btn =>{btn.removeAttribute('onclick');btn.addEventListener('click', reloadPage);});document.querySelectorAll('[onclick*="continueTopic"]').forEach(btn =>{const topicId = btn.getAttribute('data-topic-id') || btn.closest('[data-topic-id]')?.dataset.topicId;btn.removeAttribute('onclick');btn.addEventListener('click', () => continueTopic(topicId));});document.querySelectorAll('[onclick*="startTopic"]').forEach(btn =>{const topicId = btn.getAttribute('data-topic-id') || btn.closest('[data-topic-id]')?.dataset.topicId;btn.removeAttribute('onclick');btn.addEventListener('click', () => startTopic(topicId));});document.querySelectorAll('[onclick*="startPractice"]').forEach(btn =>{const topicId = btn.getAttribute('data-topic-id') || btn.closest('[data-topic-id]')?.dataset.topicId;btn.removeAttribute('onclick');btn.addEventListener('click', () => startPractice(topicId));});document.querySelectorAll('[onclick*="showTheory"]').forEach(btn =>{const topicId = btn.getAttribute('data-topic-id') || btn.closest('[data-topic-id]')?.dataset.topicId;btn.removeAttribute('onclick');btn.addEventListener('click', () => showTheory(topicId));});document.querySelectorAll('[onclick*="filterTasks"]').forEach(btn =>{const difficulty = btn.getAttribute('data-difficulty') || 'all';btn.removeAttribute('onclick');btn.addEventListener('click', () => filterTasks(difficulty));});}